import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core
import withStyles from "@material-ui/core/styles/withStyles";
// core components
import GridContainer from "components/Grid/GridContainer.jsx";
import enMsg from "__helpers/locale/en/en";
import {
    Button,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TablePagination,
    TableRow,
    Link as MaterialLink,
    CircularProgress,
    FormGroup,
    FormControl,
} from "@material-ui/core";
import CardBody from "components/Card/CardBody.jsx";
import dashboardStyle from "assets/jss/material-dashboard-react/views/dashboardStyle.jsx";
import { userService } from "_services/user.service";
import {
} from "__helpers/constants";
import { connect } from "react-redux";
import moment from "moment";
import {
    PER_PAGE_OPTIONS,
    RECORD_PER_PAGE,
} from "__helpers/constants";
import * as am4core from "@amcharts/amcharts4/core";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
import { MuiPickersUtilsProvider, KeyboardDatePicker } from "@material-ui/pickers"
import { FormErrors } from "components/Login/FormErrors"
import DateFnsUtils from "@date-io/date-fns";
import AddProject from "./ProjectPopup"

am4core.useTheme(am4themes_animated);
const columns = [
    { id: "projectName", label: "Project Name" },
    { id: "projectDesc", label: "Project Description" },
    { id: "createDate", label: "Created Date" },
    { id: "projectDesc", label: "Total Tasks" },
    { id: "projectDesc", label: "Tasks List" },
];
const ref = React.createRef();
function mapDispatchToProps(dispatch) {
    return {
        // addArticle: article => dispatch(addArticle(article))
        // taskListArr: projects => dispatch(taskListArr(projects))
    };
}
const mapStateToProps = state => {
    return {
        userInfo: state.userInfo,
        timestamp: state.timestamp,
        campaings: state.campaings,
        reduxLoadFlag: state.reduxLoadFlag,
        taskListArr: state.taskList,
    };
};
class TasksClass extends React.Component {
    constructor(props) {
        super(props);
        this.dateUtility = new DateFnsUtils();
        this._isMounted = false;
        this.state = {
            value: 0,
            page: 0,
            loading: false,
            rowsPerPageT: RECORD_PER_PAGE,
            rowsPerPage: RECORD_PER_PAGE,
            reduxLoadFlag: false,
            openProjects: false,
            taskList: this.props.taskListArr ? this.props.taskListArr : [],
        };
        this.handleChangePage = this.handleChangePage.bind(this);
        this.handleChangeRowsPerPage = this.handleChangeRowsPerPage.bind(this);
        this.confirmModalClose = this.confirmModalClose.bind(this);
    }

    componentDidMount() {
    }
    getToday(dateValue, server) {
        const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
            "Jul", "Aug", "Sept", "Oct", "Nov", "Dec"
        ];
        let today = new Date(dateValue);
        let dd = today.getDate();
        let mm = today.getMonth() + 1;
        let mName = monthNames[today.getMonth()];
        const yyyy = today.getFullYear();
        const hh = today.getHours();
        const min = today.getMinutes();
        const sec = today.getSeconds();
        if (dd < 10) {
            dd = `0${dd}`;
        }
        if (mm < 10) {
            mm = `0${mm}`;
        }
        if (server) {
            return `${dd}-${mm}-${yyyy}`;
        }
        return `${dd} ${mName} ${hh}:${min}:${sec}`;
    }
    // componentDidUpdate(oldProps) {
    //   if (oldProps.paddingRight !== this.props.paddingRight) {
    //     this.chart.paddingRight = this.props.paddingRight;
    //   }

    //   if (this.props.reduxLoadFlag != undefined && this.state.reduxLoadFlag != this.props.reduxLoadFlag) {
    //     let campaings = [];
    //     let userInfo = {};
    //     if (this.props.campaings) {
    //       let campaingsList = this.props.campaings;
    //       campaings = (campaingsList) ? campaingsList : [];
    //       if (this.chart) {
    //         this.chart.data = campaings;
    //       }
    //     }
    //     this.setState({
    //       pollutionData: campaings,
    //     })
    //   }
    // }
    handleChangePage = async (event, newPage) => {
        this.setState({
            page: newPage
        });
        // setPage(newPage);
    };
    handleChangeRowsPerPage(event) {
        this.setState({
            rowsPerPage: +event.target.value,
            page: 0
        });
    }
    validateField = (fieldName, fieldValue) => {
        let fieldValidationErrors = this.state.formErrors;
        let startDateValid = this.state.startDateValid;
        let endDateValid = this.state.endDateValid;

        switch (fieldName) {
            case "startDate":
                startDateValid = (fieldValue && fieldValue != "") ? true : false;
                fieldValidationErrors.startDate = !startDateValid
                    ? enMsg.startDateRequiredMsg
                    : "";
                break;
            case "endDate":
                endDateValid = (fieldValue && fieldValue != "") ? true : false;
                fieldValidationErrors.endDate = !endDateValid
                    ? enMsg.endDateRequiredMsg
                    : "";
                break;
        }
        this.setState({
            formErrors: fieldValidationErrors,
            startDateValid: startDateValid,
            endDateValid: endDateValid,
        }, this.validateForm);
    }
    validateForm() {
        return (
            this.state.startDateValid &&
            this.state.endDateValid
        )
    }
    openProjects() {
        this.setState({ openProjects: true })
    }
    confirmModalClose() {
        this.setState({
            openProjects: false,
            taskList: this.props.taskListArr,
        })
    }
    render() {
        const { classes } = this.props;
        const test = "test-cover";
        const test1 = "test-cover1";
        const test2 = "test-cover2";
