import React, { useState, useEffect } from 'react'
import {
    Dialog,
    DialogActions,
    DialogContent,
    Select,
    DialogTitle,
    IconButton,
    TextField,
    MenuItem,
    InputLabel,
    Button,
    FormGroup,
} from "@material-ui/core";
import GridContainer from "components/Grid/GridContainer.jsx";
import CloseIcon from '@material-ui/icons/Close';
import CircularProgress from "@material-ui/core/CircularProgress";
import { FormErrors } from "components/Login/FormErrors";
const AddProject = (props) => {
    const { openProjects, confirmModalClose, loading } = props;
    const errorList = {
        amount: ""
    }
    const [getProject, setProject] = useState("")
    const [getError, setError] = useState(errorList)
    const [getProjectValid, setProjectValid] = useState(false)
    function validateForm() {
        return getProjectValid;
    }
    function submit() {
        return getProjectValid;
    }
    const handleInput = (e) => {
        const { name, value } = e.target;
        setProject(value);
    }
    return (
        <Dialog
            className="call-modal"
            maxWidth={'sm'}
            open={openProjects}
        >
            <DialogTitle className="add-modal-title" id="discharge-planner-dialog-title">
                Create Project
                <IconButton aria-label="close">
                    <CloseIcon onClick={confirmModalClose} />
                </IconButton>
            </DialogTitle>
            <DialogContent>
                <GridContainer>
                    <div className="dashTimePanel dashTimePanel2">
                        <FormGroup className="setting_cards fix-height p-10">
                            <TextField
                                InputLabelProps={{ className: "required-label" }}
                                id="standard-basic"
                                label="Project Name"
                                className="modal_desc model_width"
                                name="amount"
                                value={getProject}
                                onChange={e => handleInput(e)} />
                            <FormErrors
                                show={getProject ? true : false}
                                formErrors={getError}
                                fieldName="amount"
                            />
                        </FormGroup>
                    </div>
                </GridContainer>
                <div className="dollar_modal">
                    <TextField
                        id="standard-basic"
                        label="Reward Amount"
                        className="modal_desc model_width"
                        name="amount"
                        value={getProject}
                        onChange={e => handleInput(e)} />
                    <FormErrors
                        show={getProject ? true : false}
                        formErrors={getError}
                        fieldName="amount"
                    />
                </div>
            </DialogContent>
            <DialogActions className="modal-actions" justify="center" >
                <Button type="button" className="btn1" onClick={submit} disabled={!validateForm}>
                    {loading && (
                        <CircularProgress
                            size={24}
                            className="buttonProgress"
                        />
                    )}
                    Update
                </Button>
                <Button color="primary" className="btn2" onClick={confirmModalClose}>
                    Cancel
                </Button>
            </DialogActions>
        </Dialog>
    )
}

export default AddProject
